const alerts = [];

for (const item of items) {
  const data = item.json;

  /* Example processing: Trigger alert if animal count falls below threshold or if distress signals detected */
  if (data.animalCount && data.animalCount < 5) {
    alerts.push({
      json: {
        alertType: 'Low Population Count',
        species: data.species,
        location: data.location,
        animalCount: data.animalCount,
        timestamp: data.timestamp
      }
    });
  }

  if (data.distressSignal === true) {
    alerts.push({
      json: {
        alertType: 'Distress Signal',
        species: data.species,
        location: data.location,
        timestamp: data.timestamp
      }
    });
  }
}

return alerts.length ? alerts : [];

