const completedTasks = items.filter(item => {
  return item.json.completed === true;
});

return [{
  json: {
    totalTasks: items.length,
    completedTasksCount: completedTasks.length,
    completionRate: items.length === 0 ? 0 : (completedTasks.length / items.length) * 100
  }
}];

const teamsSentiment = $items("AI Sentiment Analysis MS Teams")[0]?.json?.choices?.[0]?.text || '';
const trelloCompletion = $items("Calculate Task Completion")[0].json?.completionRate || 0;
const asanaCompletion = $items("Calculate Asana Task Completion")[0].json?.completionRate || 0;
const avgCompletion = (trelloCompletion + asanaCompletion) / 2;

// Determine micro-break recommendations based on sentiment and average completion rate
let activities = [];
const sentimentLower = teamsSentiment.toLowerCase();

if (sentimentLower.includes('negative') || avgCompletion < 50) {
  activities = [
    'Take a 5-minute mindfulness breathing break',
    'Stand up and stretch',
    'Grab a glass of water and hydrate'
  ];
} else if (sentimentLower.includes('neutral')) {
  activities = [
    'Quick desk yoga poses for 3 minutes',
    'Look away from the screen for 2 minutes',
    'Write down a quick gratitude note'
  ];
} else {
  activities = [
    'Celebrate a recent success with a quick dance',
    'Share a positive story with the team',
    'Plan a virtual coffee chat'
  ];
}

const motivationalMessages = [
  "Keep up the great work! Your efforts make a difference.",
  "Remember to take breaks — they boost productivity.",
  "You're doing amazing, keep pushing forward!",
  "Small steps each day lead to big achievements.",
  "Stay positive and focused — success is near."
];

const randomActivity = activities[Math.floor(Math.random() * activities.length)];
const randomMotivation = motivationalMessages[Math.floor(Math.random() * motivationalMessages.length)];

return [{
  json: {
    teamsSentiment: teamsSentiment.trim(),
    averageCompletionRate: avgCompletion,
    suggestedActivity: randomActivity,
    motivationalMessage: randomMotivation
  }
}];

