const carbonData = items[0].json;
const tempData = items[1].json;

// Aggregate data by year
const aggregated = {};

carbonData.forEach(entry => {
  const year = entry.year;
  if(!aggregated[year]) {
    aggregated[year] = {carbonEmissions: 0, tempChange: 0, countTemp: 0};
  }
  aggregated[year].carbonEmissions += entry.emission;
});

tempData.forEach(entry => {
  const year = entry.year;
  if(!aggregated[year]) {
    aggregated[year] = {carbonEmissions: 0, tempChange: 0, countTemp: 0};
  }
  aggregated[year].tempChange += entry.change;
  aggregated[year].countTemp += 1;
});

// Average temperature change if multiple entries
for (const year in aggregated) {
  if(aggregated[year].countTemp > 0) {
    aggregated[year].tempChange = aggregated[year].tempChange / aggregated[year].countTemp;
  }
}

// Prepare output
const result = Object.keys(aggregated).map(year => {
  return {
    year: year,
    carbonEmissions: aggregated[year].carbonEmissions,
    temperatureChange: aggregated[year].tempChange
  };
});

return result.map(r => ({ json: r }));

const data = items.map(item => item.json);

let report = '# Climate Impact Report\n\n';
report += 'Year | Carbon Emissions (MtCO2e) | Temperature Change (Â°C)\n';
report += '--- | --- | ---\n';
for (const entry of data) {
  report += `${entry.year} | ${entry.carbonEmissions.toFixed(2)} | ${entry.temperatureChange.toFixed(2)}\n`;
}

report += '\n*Generated automatically by the Automated Climate Impact Tracker.*';

return [
  {
    json: { report }
  }
];
