/*
This node checks the blockchain for the credential status.
Input: credentialId
Output: { status: 'active' | 'revoked' | 'updated', details: {...} }
*/

const credentialId = $json["credentialId"];

// Placeholder for blockchain API call
// Replace with actual API call to blockchain node or service

async function checkCredentialStatus(id) {
  // Simulated response
  return {
    status: Math.random() > 0.8 ? "revoked" : "active",
    details: {
      credentialId: id,
      issuedTo: "user@example.com",
      issuedAt: "2023-01-01T00:00:00Z"
    }
  };
}

return checkCredentialStatus(credentialId).then(result => ({ json: result }));

/*
