const topic = 'AI-Driven Community SkillSwap Scheduler & Automated Matchmaking System';
const repoTopics = items[0].json.topics || [];
if (repoTopics.includes(topic)) {
  return [{ json: { topicExists: true } }];
} else {
  return [{ json: { topicExists: false } }];
}

if (items[0].json.topicExists) {
  throw new Error('Topic already exists in the repository. Workflow halted.');
}
return items;

const users = items.map(item => item.json);

// Simple matchmaking logic based on matching skillsOffered and skillsToLearn and location proximity
const matches = [];

for (let i = 0; i < users.length; i++) {
  for (let j = i + 1; j < users.length; j++) {
    const userA = users[i];
    const userB = users[j];

    const skillsAOffers = userA.skillsOffered || [];
    const skillsAToLearn = userA.skillsToLearn || [];
    const skillsBOffers = userB.skillsOffered || [];
    const skillsBToLearn = userB.skillsToLearn || [];

    const locationA = userA.location || '';
    const locationB = userB.location || '';

    // Basic location match check (could be improved with geo proximity)
    if (locationA !== locationB) continue;

    // Check if User A offers skills User B wants to learn and vice versa
    const aCanTeachB = skillsAOffers.some(skill => skillsBToLearn.includes(skill));
    const bCanTeachA = skillsBOffers.some(skill => skillsAToLearn.includes(skill));

    if (aCanTeachB && bCanTeachA) {
      matches.push({
        userA: userA.name,
        userB: userB.name,
        location: locationA,
        matchedSkills: {
          aToB: skillsAOffers.filter(skill => skillsBToLearn.includes(skill)),
          bToA: skillsBOffers.filter(skill => skillsAToLearn.includes(skill))
        }
      });
    }
  }
}

return matches.map(match => ({ json: match }));
