const trafficRoutes = $items()[0].json.routes || [];
const transitRoutes = $items()[1].json.routes || [];

function carbonEmission(mode, distance) {
  // Emission in kg CO2 per km
  const emissionFactors = {
    car: 0.192,      // average petrol car
    bus: 0.105,      // average bus per passenger
    train: 0.041,    // average train per passenger
    bike: 0,
    walk: 0
  };
  return (emissionFactors[mode] || 0) * distance;
}

// Combine and calculate emissions
const suggestRoutes = [];

trafficRoutes.forEach(route => {
  const dist = route.distance_km || 0;
  suggestRoutes.push({
    mode: 'car',
    distance: dist,
    duration: route.duration_min,
    carbonEmissionKg: carbonEmission('car', dist),
    routeDetails: route
  });
});

transitRoutes.forEach(route => {
  route.legs.forEach(leg => {
    const mode = leg.mode.toLowerCase();
    const dist = leg.distance_km || 0;
    suggestRoutes.push({
      mode: mode,
      distance: dist,
      duration: leg.duration_min,
      carbonEmissionKg: carbonEmission(mode, dist),
      routeDetails: leg
    });
  });
});

// Sort by lowest carbon emissions
suggestRoutes.sort((a, b) => a.carbonEmissionKg - b.carbonEmissionKg);

return [{ json: { suggestedRoutes: suggestRoutes.slice(0, 3) } }];
