parameters.functionCode:
const { origin, destination, constraints } = items[0].json;

// Basic validation
if (!origin || !destination) {
  throw new Error('Origin and destination are required');
}

return [{ json: { origin, destination, constraints: constraints || {} } }];

const routeInfo = items[0].json.features[0];

// Calculate approximate carbon emissions based on distance and travel mode (assume walking/hiking has near zero emissions)

const distanceMeters = routeInfo.properties.summary.distance;
const distanceKm = distanceMeters / 1000;

// For demonstration: walking emissions ~0 g/km
const carbonEmissionsGrams = 0;

return [{ json: { route: routeInfo, carbonEmissionsGrams, distanceKm } }];

const distanceKm = items[0].json.distanceKm;

// Assume $10 per ton CO2 offset cost
// 1 ton = 1,000,000 grams
const costPerGram = 10 / 1000000;
const carbonOffsetCost = items[0].json.carbonEmissionsGrams * costPerGram;

// For walking, zero emissions means no cost

return [{ json: { carbonOffsetCost: carbonOffsetCost.toFixed(4), carbonOffsetOptions: [
  {
    option: 'Plant Trees',
    description: 'Purchase tree planting offsets to compensate carbon footprint.',
    estimatedCostUSD: carbonOffsetCost.toFixed(4)
  },
  {
    option: 'Renewable Energy Credits',
    description: 'Support renewable energy projects offsetting your travel carbon.',
    estimatedCostUSD: carbonOffsetCost.toFixed(4)
  }
] } }];

