parameters.functionCode:
const parser = require('xml2js').parseString;

return new Promise((resolve, reject) => {
  parser(items[0].json, { mergeAttrs: true, explicitArray: false }, (err, result) => {
    if (err) {
      reject(err);
    } else {
      const entries = result.rss.channel.item;
      const papers = Array.isArray(entries) ? entries : [entries];
      const formatted = papers.map(paper => ({
        title: paper.title || "",
        link: paper.link || "",
        pubDate: paper.pubDate || "",
        description: paper.description || "",
        authorsRaw: paper['dc:creator'] || "",
        authors: paper['dc:creator'] ? paper['dc:creator'].split(/,|and/).map(s => s.trim()) : []
      }));
      resolve(formatted.map(p => ({ json: p })));
    }
  });
});

const keywords = [
  "entanglement", "quantum algorithms", "quantum error correction", "quantum cryptography", "quantum annealing", "superconducting qubits", "topological quantum computing", "quantum machine learning", "quantum simulation", "quantum supremacy"
];

return items.map(item => {
  const text = (item.json.title + ' ' + item.json.description).toLowerCase();
  const foundKeywords = keywords.filter(keyword => text.includes(keyword));
  item.json.topics = foundKeywords;

  // Extract collaboration triggers if multiple authors or notable keywords exist
  item.json.collaborationOpportunity = false;
  if (item.json.authors.length > 1) {
    item.json.collaborationOpportunity = true;
  }
  if (foundKeywords.length > 0) {
    item.json.collaborationOpportunity = true;
  }

  return item;
});

