parameters.functionCode:
const topicsCounts = {};

for (const item of items) {
  const topics = item.json.topics || [];
  for (const topic of topics) {
    topicsCounts[topic] = (topicsCounts[topic] || 0) + 1;
  }
}

return [{ json: { topicsCounts } }];
