parameters.functionCode:
const papers = $items("Fetch Research Papers")[0].json.message.items;
const conferences = $items("Fetch Conference Feeds")[0].json.events || [];
const repos = $items("Fetch Collaboration Platforms (GitHub)")[0].json.items || [];

// Extract keyword frequency from titles and descriptions
const keywordCount = {};

function countKeywords(text) {
  const keywords = ['quantum computing', 'qubit', 'quantum algorithm', 'quantum hardware', 'quantum error correction'];
  const lowerText = text.toLowerCase();
  keywords.forEach(keyword => {
    if (lowerText.includes(keyword)) {
      keywordCount[keyword] = (keywordCount[keyword] || 0) + 1;
    }
  });
}

papers.forEach(paper => {
  countKeywords(paper.title || '');
  countKeywords(paper.abstract || '');
});

conferences.forEach(conf => {
  countKeywords(conf.title || '');
  countKeywords(conf.description || '');
});

repos.forEach(repo => {
  countKeywords(repo.description || '');
  countKeywords(repo.name || '');
});

return [{
  json: {
    papersCount: papers.length,
    conferencesCount: conferences.length,
    repositoriesCount: repos.length,
    keywordFrequency: keywordCount
  }
}];

parameters.text:
={{
  `Latest Quantum Computing Research Data:

` +
  `Papers fetched: ${$json["papersCount"]}
` +
  `Upcoming conferences: ${$json["conferencesCount"]}
` +
  `Active repositories: ${$json["repositoriesCount"]}

` +
  `Keyword frequency highlights:
` +
  Object.entries($json["keywordFrequency"]).map(([key, val]) => `- ${key}: ${val}`).join('\n')
}}

parameters.functionCode:
return items.map(item => {
  const summaryLines = [];
  summaryLines.push(`Summary for Quantum Computing Research Collaboration and Innovation Tracker:`);
  summaryLines.push(`- Total new papers: ${item.json.papersCount}`);
  summaryLines.push(`- Upcoming conferences: ${item.json.conferencesCount}`);
  summaryLines.push(`- New collaborations (Repositories): ${item.json.repositoriesCount}`);
  summaryLines.push(`- Key terms frequency:`);
  for (const [term, freq] of Object.entries(item.json.keywordFrequency || {})) {
    summaryLines.push(`  * ${term}: ${freq}`);
  }

  item.json.summary = summaryLines.join('\n');
  return item;
});
