const arxiv = items[0].json.entries || [];
const semanticScholar = items[1].json.data || [];
const pubmed = items[2].json.results || [];

// Normalize and extract relevant data
const combined = [];

arxiv.forEach(paper => {
  combined.push({
    source: 'ArXiv',
    title: paper.title,
    abstract: paper.summary,
    authors: paper.authors.map(a => a.name).join(', '),
    publishedDate: paper.published,
    link: paper.id
  });
});

semanticScholar.forEach(paper => {
  combined.push({
    source: 'SemanticScholar',
    title: paper.title,
    abstract: paper.abstract || '',
    authors: (paper.authors || []).map(a => a.name).join(', '),
    publishedDate: paper.year ? paper.year.toString() : '',
    link: `https://semanticscholar.org/paper/${paper.paperId || ''}`
  });
});

pubmed.forEach(paper => {
  combined.push({
    source: 'PubMed',
    title: paper.title,
    abstract: paper.abstract || '',
    authors: paper.authors ? paper.authors.join(', ') : '',
    publishedDate: paper.pubdate || '',
    link: paper.url || ''
  });
});

return combined.map(data => ({ json: data }));

const preferences = $input.item.json;

// Sample input structure: { topics: ['error correction', 'quantum algorithms'], frequency: 'daily', contact: { email: 'user@example.com', slackWebhook: 'https://hooks.slack.com/services/...' } }

// Filter trend analysis results by user topics
// This node expects previous node output with trend analysis text

const trendText = $input.all()[0].json.choices[0].message.content.toLowerCase();

const matchesTopic = preferences.topics.some(topic => trendText.includes(topic.toLowerCase()));

return matchesTopic ? $input.all() : [];

// Provide user preferences input placeholder
return [
  {
    json: {
      topics: ["quantum algorithms", "error correction", "quantum communication"],
      frequency: "daily",
      contact: {
        email: "user@example.com",
        slackWebhook: "https://hooks.slack.com/services/XXXXX/YYYYY/ZZZZZ"
      }
    }
  }
];
