const parseString = require('xml2js').parseStringPromise;

async function parseArxivData(xmlData) {
  try {
    const result = await parseString(xmlData);
    const entries = (result.feed.entry || []).map(entry => {
      return {
        title: entry.title[0].trim(),
        summary: entry.summary[0].trim(),
        published: entry.published[0],
        authors: entry.author.map(a => a.name[0]),
        link: entry.id[0]
      };
    });
    return entries;
  } catch (error) {
    return [];
  }
}

return parseArxivData(items[0].json.body).then(data => ({json: {papers: data}}));

const ieeeArticles = items[0].json.articles || [];

const papers = ieeeArticles.map(article => {
  return {
    title: article.title,
    summary: article.abstract || '',
    published: article.publication_date || article.sort_date || '',
    authors: article.authors ? article.authors.map(a => a.full_name) : [],
    link: article.pdf_url || article.document_link || ''
  };
});
return [{json: {papers}}];

const arxivPapers = items[0].json.papers || [];
const ieeePapers = items[1].json.papers || [];

// Merge and remove duplicates by title
const map = new Map();
[...arxivPapers, ...ieeePapers].forEach(paper => {
  if (!map.has(paper.title)) {
    map.set(paper.title, paper);
  }
});
const mergedPapers = Array.from(map.values());

return [{json: {mergedPapers}}];

const nlp = require('compromise');

function extractTrends(papers) {
  const topicFreq = {};

  papers.forEach(paper => {
    const text = (paper.title + ' ' + paper.summary).toLowerCase();

    // Simple NLP: extract nouns and noun-phrases
    const doc = nlp(text);
    const nouns = doc.nouns().out('frequency');

    nouns.forEach(item => {
      if (item.normal.length > 2) {
        topicFreq[item.normal] = (topicFreq[item.normal] || 0) + item.count;
      }
    });
  });

  // Sort topics by frequency
  const sortedTopics = Object.entries(topicFreq)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 10)
    .map(e => e[0]);

  return sortedTopics;
}

const trends = extractTrends(items[0].json.mergedPapers);
return [{json: {trendingTopics: trends}}];

const papers = items[0].json.mergedPapers;
const trends = items[1].json.trendingTopics;

function summarizePapers(papers, trends) {
  const trendSummaries = trends.map(trend => {
    const relatedPapers = papers.filter(p => {
      const text = (p.title + ' ' + p.summary).toLowerCase();
      return text.includes(trend);
    });

    const titles = relatedPapers.slice(0, 3).map(p => `- ${p.title}`);

    return `Trend: "${trend}"
Related Papers:
${titles.join('\n')}
`;
  });

  return trendSummaries.join('\n\n');
}

const summary = summarizePapers(papers, trends);

return [{json: {summary}}];
