const parseArxivXml = (xml) => {
  const parser = new DOMParser();
  const xmlDoc = parser.parseFromString(xml, 'text/xml');
  const entries = xmlDoc.getElementsByTagName('entry');
  let results = [];
  for (let i = 0; i < entries.length; i++) {
    const entry = entries[i];
    results.push({
      title: entry.getElementsByTagName('title')[0]?.textContent || '',
      summary: entry.getElementsByTagName('summary')[0]?.textContent || '',
      published: entry.getElementsByTagName('published')[0]?.textContent || '',
      link: entry.getElementsByTagName('id')[0]?.textContent || ''
    });
  }
  return results;
};

const arxivRaw = items[0].json;
const xmlString = arxivRaw.toString();

// Since DOMParser doesn't exist in Node, use a regex fallback parsing for demo
let entries = [];
const entryRegex = /<entry>([\s\S]*?)<\/entry>/g;
let match;
while ((match = entryRegex.exec(xmlString)) !== null) {
  const entryText = match[1];
  const getText = (tag) => {
    const regex = new RegExp(`<${tag}>([\s\S]*?)<\/${tag}>`);
    const res = regex.exec(entryText);
    return res ? res[1].replace(/\n/g, ' ').trim() : '';
  };
  entries.push({
    title: getText('title'),
    summary: getText('summary'),
    published: getText('published'),
    link: getText('id')
  });
}

return entries.map(i => ({ json: i }));

const itemsGoogleScholar = items[0].json['organic_results'] || [];
const results = itemsGoogleScholar.slice(0, 5).map(item => ({
  title: item.title || '',
  snippet: item.snippet || '',
  link: item.link || ''
}));
return results.map(i => ({ json: i }));

const articles = items.map(i => i.json).flat();
const newsItems = articles.map(n => ({
  title: n.title || '',
  description: n.description || '',
  url: n.url || '',
  publishedAt: n.publishedAt || ''
}));
return newsItems.map(i => ({ json: i }));

const allEntries = [];

// Pull data arrays from previous nodes
const arxivPapers = $items('Parse arXiv XML');
const scholarPapers = $items('Extract Google Scholar Data');
const newsArticles = $items('Extract NewsAPI Data');

arxivPapers.forEach(item => allEntries.push({
  source: 'arXiv',
  title: item.json.title,
  summary: item.json.summary,
  link: item.json.link,
  published: item.json.published
}));
scholarPapers.forEach(item => allEntries.push({
  source: 'Google Scholar',
  title: item.json.title,
  summary: item.json.snippet,
  link: item.json.link
}));
newsArticles.forEach(item => allEntries.push({
  source: 'News',
  title: item.json.title,
  summary: item.json.description,
  link: item.json.url,
  published: item.json.publishedAt
}));

return allEntries.map(i => ({ json: i }));
