const parseString = require('xml2js').parseString;
const xml = items[0].binary.data.toString();

return new Promise((resolve, reject) => {
  parseString(xml, { explicitArray: false }, (err, result) => {
    if (err) return reject(err);

    const channel = result.rss?.channel;
    const entries = [];

    if (channel?.item) {
      if (Array.isArray(channel.item)) {
        channel.item.forEach(item => {
          entries.push({
            json: {
              title: item.title,
              link: item.link,
              pubDate: new Date(item.pubDate).toISOString(),
              description: item.description,
              source: items[0].json.url
            }
          });
        });
      } else {
        entries.push({
          json: {
            title: channel.item.title,
            link: channel.item.link,
            pubDate: new Date(channel.item.pubDate).toISOString(),
            description: channel.item.description,
            source: items[0].json.url
          }
        });
      }
    }
    resolve(entries);
  });
});

const natural = require('natural');
const categories = [
  { name: 'Research Papers', keywords: ['paper', 'arxiv', 'research', 'journal'] },
  { name: 'News', keywords: ['news', 'announcement', 'update'] },
  { name: 'Articles', keywords: ['article', 'opinion', 'blog'] },
  { name: 'Tutorials', keywords: ['tutorial', 'guide', 'how-to'] }
];

return items.map(item => {
  const title = item.json.title.toLowerCase();
  const description = (item.json.description || '').toLowerCase();
  let category = 'Other';

  for (const cat of categories) {
    for (const keyword of cat.keywords) {
      if (title.includes(keyword) || description.includes(keyword)) {
        category = cat.name;
        break;
      }
    }
    if (category !== 'Other') break;
  }

  return {
    json: {
      ...item.json,
      category
    }
  };
});

const openai = require('openai');

const configuration = new openai.Configuration({
  apiKey: this.getCredentials('openAIApi').apiKey,
});

const openaiClient = new openai.OpenAIApi(configuration);

async function summarize(text) {
  const response = await openaiClient.createChatCompletion({
    model: 'gpt-4',
    messages: [
      { role: 'system', content: 'You are a helpful assistant that summarizes quantum computing research papers and articles into concise bullet points.' },
      { role: 'user', content: `Summarize the following content:
${text}` }
    ],
    max_tokens: 150,
    temperature: 0.3
  });
  return response.data.choices[0].message.content.trim();
}

const summarizedItems = [];

for (const item of items) {
  const contentToSummarize = item.json.description || item.json.title || '';
  const summary = await summarize(contentToSummarize);
  summarizedItems.push({
    json: {
      ...item.json,
      summary
    }
  });
}

return summarizedItems;

