const papers = items.map(item => {
  return {
    title: item.json.title,
    abstract: item.json.summary,
    authors: item.json.authors,
    url: item.json.id,
    published: item.json.published
  };
});

return papers.map(paper => ({ json: paper }));

const embeddings = items.map(item => item.json.embedding);

// Simulate clustering or trend detection by grouping papers by keywords or semantic similarity
// For the sake of the example, just assign a cluster randomly

items.forEach((item, index) => {
  const clusters = ['Quantum Algorithms', 'Quantum Hardware', 'Quantum Error Correction', 'Quantum Cryptography'];
  const assignedCluster = clusters[index % clusters.length];
  item.json.trendCluster = assignedCluster;
});

return items;

const interests = items[0].json;
const papers = items[1].json;

const alerts = [];

interests.forEach(researcher => {
  const matchedPapers = papers.filter(paper => {
    return researcher.keywords.some(k => paper.trendCluster.toLowerCase().includes(k.toLowerCase()) || paper.title.toLowerCase().includes(k.toLowerCase()) || paper.abstract.toLowerCase().includes(k.toLowerCase()));
  });

  if (matchedPapers.length > 0) {
    alerts.push({
      researcherEmail: researcher.email,
      matchedPapers
    });
  }
});

return alerts.map(alert => ({ json: alert }));

