const weather = items[0].json.current;
const events = items[1].json;
const trends = items[2].json.trends;

// Extract today's occasion based on current date
const today = new Date().toISOString().substring(0, 10);
const todayEvents = events.filter(event => event.start.dateTime && event.start.dateTime.startsWith(today));
const occasion = todayEvents.length > 0 ? todayEvents[0].summary : "Casual";

// Simple outfit suggestion logic based on weather and occasion
let outfitSuggestions = [];

if (weather.temp_c < 10) {
  outfitSuggestions.push("Warm jacket, scarf, and sustainable boots.");
} else if (weather.temp_c >= 10 && weather.temp_c < 20) {
  outfitSuggestions.push("Light jacket and eco-friendly sneakers.");
} else {
  outfitSuggestions.push("Breathable cotton t-shirt and shorts.");
}

if (occasion.toLowerCase().includes("formal")) {
  outfitSuggestions.push("Add a recycled fabric formal blazer.");
} else if (occasion.toLowerCase().includes("party")) {
  outfitSuggestions.push("Stylish eco-friendly party dress or shirt.");
}

// Integrate fashion trends
outfitSuggestions.push("Incorporate these trending sustainable colors and materials: " + trends.join(", "));

return [{ json: {
  occasion: occasion,
  weather: weather,
  outfitSuggestions: outfitSuggestions
} }];

return [{ json: {
  tips: [
    "Donate unused clothes to reduce waste.",
    "Opt for clothes made from organic or recycled materials.",
    "Repair and upcycle old garments instead of discarding.",
    "Choose versatile pieces that can be mixed and matched.",
    "Support brands with transparent and ethical supply chains."
  ]
} }];
