const twitterData = items[0].json;
const instagramData = items[1].json;
const facebookData = items[2].json;

// Aggregate data
const engagement = {likes:0,shares:0,comments:0};

// Twitter
engagement.likes += twitterData.public_metrics ? twitterData.public_metrics.like_count : 0;
engagement.shares += twitterData.public_metrics ? twitterData.public_metrics.retweet_count : 0;
engagement.comments += twitterData.public_metrics ? twitterData.public_metrics.reply_count : 0;

// Instagram
engagement.likes += instagramData.media_like_count || 0;
engagement.comments += instagramData.media_comment_count || 0;

// Facebook
facebookData.data.forEach(post => {
  engagement.likes += post.likes ? post.likes.summary.total_count : 0;
  engagement.shares += post.shares ? post.shares.count : 0;
  engagement.comments += post.comments ? post.comments.summary.total_count : 0;
});

return [{json:{engagement}}];
